#!/bin/bash

#This file is transferred to the Cumulus VX and executed to re-map interfaces
#Extra config COULD be added here but I would recommend against that to keep this file standard.
echo "#################################"
echo "  Running Switch Post Config"
echo "#################################"
sudo su

echo "  Adding admin user"
useradd -G wheel -s /bin/bash admin

echo "  Configuring interfaces"
mkdir -p /etc/net/ifaces/vagrant
cat <<EOF > /etc/net/ifaces/vagrant/options
BOOTPROTO=dhcp
TYPE=eth
CONFIG_WIRELESS=no
EOF
cat <<EOF > /etc/net/ifaces/eth0/options
BOOTPROTO=dhcp
TYPE=eth
CONFIG_WIRELESS=no
EOF

## Convenience code. This is normally done in ZTP.
echo "  Configuring SSH keys"
mkdir -p /root/.ssh
mkdir -p /home/admin/.ssh
echo "ssh-rsa " >> /root/.ssh/authorized_keys
echo "ssh-rsa " >> /home/admin/.ssh/authorized_keys
chmod 700 -R /root/.ssh
chmod 700 -R /home/admin/.ssh

echo "#################################"
echo "   Finished"
echo "#################################"
